import React, { useState } from "react";
import Lightbox from "yet-another-react-lightbox";
import "yet-another-react-lightbox/styles.css";
import styled from "styled-components";

const GalleryContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
  gap: 16px;
  margin-top: 20px;
`;

const GalleryImageWrapper = styled.div`
  position: relative;
  overflow: hidden;
  border-radius: 8px;
  cursor: pointer;

  img {
    width: 100%;
    height: auto;
    display: block;
    transition: transform 0.3s ease, opacity 0.3s ease;
    border-radius: 8px;
  }

  &:hover img {
    transform: scale(1.05);
    opacity: 0.85;
  }

  .overlay-icon {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-size: 28px;
    color: white;
    opacity: 0;
    pointer-events: none;
    transition: opacity 0.3s ease;
  }

  &:hover .overlay-icon {
    opacity: 1;
  }
`;

const GalleryLightbox = ({ images, slug }) => {
  const [index, setIndex] = useState(-1);

  if (!images || !Array.isArray(images)) return null;

  const slides = images.map((img) => ({
    src: `/images/${slug}/${img.filename}`,
    alt: img.alt || "",
  }));

  return (
    <>
      <GalleryContainer>
        {images.map((image, i) => (
          <GalleryImageWrapper key={i} onClick={() => setIndex(i)}>
            <img
              src={`/images/${slug}/${image.filename}`}
              alt={image.alt || ""}
            />
            <div className="overlay-icon">üîç</div>
          </GalleryImageWrapper>
        ))}
      </GalleryContainer>

      <Lightbox
        open={index >= 0}
        index={index}
        close={() => setIndex(-1)}
        slides={slides}
      />
    </>
  );
};

export default GalleryLightbox;
