import React from "react";
import styled from "styled-components";
import { MapPin, Heart } from "lucide-react";

const FooterWrapper = styled.footer`
  background: linear-gradient(to right, #111827, #1f2937);
  color: white;
`;

const Container = styled.div`
  max-width: 1280px;
  margin: 0 auto;
  padding: 3rem 1rem;

  @media (min-width: 640px) {
    padding: 3rem 1.5rem;
  }
`;

const Grid = styled.div`
  display: grid;
  gap: 2rem;

  @media (min-width: 768px) {
    grid-template-columns: repeat(3, 1fr);
  }
`;

const Section = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const Brand = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;

  span {
    font-size: 1.25rem;
    font-weight: bold;
  }
`;

const IconWrapper = styled.div`
  width: 2rem;
  height: 2rem;
  background: linear-gradient(to right, #9333ea, #3b82f6);
  border-radius: 9999px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Description = styled.p`
  color: #d1d5db;
  font-size: 0.875rem;
`;

const SectionTitle = styled.span`
  font-size: 1.125rem;
  font-weight: 600;
`;

const LinkText = styled.p`
  color: #d1d5db;
  font-size: 0.875rem;
  cursor: pointer;
  transition: color 0.3s ease;

  &:hover {
    color: white;
  }
`;

const ContactText = styled.p`
  color: #d1d5db;
  font-size: 0.875rem;
`;

const BottomBar = styled.div`
  border-top: 1px solid #374151;
  margin-top: 2rem;
  padding-top: 2rem;
  text-align: center;
`;

const MadeWith = styled.p`
  color: #d1d5db;
  font-size: 0.875rem;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.25rem;
`;

const Footer = () => {
  return (
    <FooterWrapper>
      <Container>
        <Grid>
          <Section>
            <Brand>
              <IconWrapper>
                <MapPin size={20} color="white" />
              </IconWrapper>
              <span>Notodden Lokalt</span>
            </Brand>
            <Description>
              Kobler deg med de beste lokale bedriftene i Notodden. Oppdag,
              utforsk og støtt ditt lokalsamfunn.
            </Description>
          </Section>

          <Section>
            <SectionTitle>Nyttige lenker</SectionTitle>
            <div>
              <LinkText>Alle bedrifter</LinkText>
              <LinkText>Legg til din bedrift</LinkText>
              <LinkText>Om oss</LinkText>
            </div>
          </Section>

          <Section>
            <SectionTitle>Kontaktinfo</SectionTitle>
            <div>
              <ContactText>Notodden, Norge</ContactText>
              <ContactText>support@notoddenlokalt.no</ContactText>
              <ContactText>+47 123 45 678</ContactText>
            </div>
          </Section>
        </Grid>

        <BottomBar>
          <MadeWith>
            <span>Laget med</span>
            <Heart size={16} color="#ef4444" fill="#ef4444" />
            <span>for Notodden-samfunnet</span>
          </MadeWith>
        </BottomBar>
      </Container>
    </FooterWrapper>
  );
};

export default Footer;
