import React, { useState } from "react";
import styled, { keyframes } from "styled-components";
import { motion } from "framer-motion";
import { Search, MapPin, Star, Users, Clock } from "lucide-react";
import BusinessCard from "@/components/BusinessCard";
import { businesses } from "@/data/businesses";

// Floating animation for decorative elements
const float = keyframes`
  0% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0); }
`;

// ---------------- Styled Components ---------------- //

const Container = styled.div`
  min-height: 100vh;
  font-family: "Segoe UI", sans-serif;
`;

const HeroSection = styled.section`
  position: relative;
  padding: 5rem 1rem;
  background: linear-gradient(to bottom right, #7c3aed, #2563eb, #4f46e5);
  overflow: hidden;
`;

const Overlay = styled.div`
  position: absolute;
  inset: 0;
  background: rgba(0, 0, 0, 0.2);
`;

const FloatingCircle = styled.div`
  position: absolute;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 9999px;
  animation: ${float} 6s ease-in-out infinite;
  ${({ top, left, right, bottom, size, delay }) => `
    top: ${top};
    left: ${left};
    right: ${right};
    bottom: ${bottom};
    width: ${size};
    height: ${size};
    animation-delay: ${delay};
  `}
`;

const Content = styled.div`
  position: relative;
  max-width: 1120px;
  margin: 0 auto;
  text-align: center;
  color: white;
`;

const GradientText = styled.span`
  display: block;
  background: linear-gradient(to right, #facc15, #fb923c);
  background-clip: text;
  -webkit-text-fill-color: transparent;
`;

const StyledInputWrapper = styled.div`
  position: relative;
  max-width: 640px;
  margin: 2rem auto;
`;

const StyledInput = styled.input`
  padding: 1rem 1rem 1rem 3rem;
  font-size: 1.125rem;
  background: rgba(255, 255, 255, 0.95);
  border-radius: 1rem;
  border: none;
  width: 100%;
  box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
`;

const IconWrapper = styled.div`
  position: absolute;
  top: 50%;
  left: 1rem;
  transform: translateY(-50%);
  color: #9ca3af;
`;

const StatsGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 1.5rem;
  max-width: 768px;
  margin: 2rem auto;

  @media (min-width: 768px) {
    grid-template-columns: repeat(4, 1fr);
  }
`;

const StatCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 1rem;
  padding: 1.5rem;
  color: white;
  text-align: center;
`;

const CategorySection = styled.section`
  padding: 2rem 1rem;
  background: rgba(255, 255, 255, 0.5);
  backdrop-filter: blur(10px);
`;

const CategoryButton = styled.button`
  padding: 0.5rem 1.5rem;
  border-radius: 9999px;
  font-weight: 500;
  border: ${({ active }) => (active ? "none" : "1px solid #a78bfa")};
  background: ${({ active }) =>
    active ? "linear-gradient(to right, #7c3aed, #2563eb)" : "#ffffff"};
  color: ${({ active }) => (active ? "white" : "#7c3aed")};
  margin: 0.5rem;
  cursor: pointer;
  transition: 0.3s;
  box-shadow: ${({ active }) =>
    active ? "0 0 8px rgba(124, 58, 237, 0.5)" : "none"};
`;

const BusinessGrid = styled.div`
  display: grid;
  gap: 2rem;
  grid-template-columns: 1fr;

  @media (min-width: 768px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (min-width: 1024px) {
    grid-template-columns: repeat(3, 1fr);
  }
`;

const CallToActionSection = styled.section`
  background: linear-gradient(to right, #7c3aed, #2563eb);
  padding: 5rem 1rem;
  text-align: center;
  color: white;
`;

const CTAButton = styled.button`
  padding: 1rem 2rem;
  font-size: 1.125rem;
  font-weight: 600;
  background: white;
  color: #7c3aed;
  border-radius: 9999px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.15);
  cursor: pointer;
  transition: 0.3s;

  &:hover {
    transform: scale(1.05);
    background: #f3f4f6;
  }
`;

// ---------------- Main Component ---------------- //

const HomePage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("Alle");

  const categories = ["Alle", ...new Set(businesses.map((b) => b.category))];

  const filteredBusinesses = businesses.filter((business) => {
    const matchesSearch =
      business.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      business.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory =
