import React from "react";
import { useParams, Link } from "react-router-dom";
import { motion } from "framer-motion";
import styled from "styled-components";
import {
  ArrowLeft,
  MapPin,
  Phone,
  Mail,
  Globe,
  Clock,
  Star,
  Calendar,
  CheckCircle,
} from "lucide-react";

import { businesses } from "../data/businesses";
import { Button } from "../components/ui/Button";
import { toast } from "../components/ui/use-toast";

// Styled Components
const PageWrapper = styled.div`
  min-height: 100vh;
  background: linear-gradient(to bottom right, #f8fafc, #eff6ff, #f5f3ff);
`;

const StickyHeader = styled.div`
  position: sticky;
  top: 4rem;
  z-index: 40;
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(12px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
`;

const Section = styled.section`
  padding: 3rem 1rem;
  @media (min-width: 640px) {
    padding: 3rem 1.5rem;
  }
  @media (min-width: 1024px) {
    padding: 3rem 2rem;
  }
`;

const BusinessTitle = styled.h1`
  font-size: 2.5rem;
  font-weight: 700;
  color: #1f2937;
  margin-bottom: 1rem;
`;

const CategoryTag = styled.span`
  display: inline-block;
  background: #ede9fe;
  color: #6b21a8;
  padding: 0.25rem 0.75rem;
  border-radius: 9999px;
  font-size: 0.875rem;
  font-weight: 500;
  margin-bottom: 1rem;
`;

const InfoCard = styled.div`
  background: white;
  border-radius: 1rem;
  box-shadow: 0 10px 15px rgba(0, 0, 0, 0.05);
  padding: 1.5rem;
`;

const DayRow = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.5rem 0.75rem;
  border-radius: 0.5rem;
  background: ${(props) => (props.highlight ? "#f3e8ff" : "transparent")};
  border: ${(props) => (props.highlight ? "1px solid #ddd6fe" : "none")};
`;

const DayText = styled.span`
  font-weight: 500;
  color: ${(props) => (props.highlight ? "#5b21b6" : "#374151")};
`;

const HourText = styled.span`
  font-weight: ${(props) => (props.highlight ? "600" : "normal")};
  color: ${(props) => (props.highlight ? "#6b21a8" : "#4b5563")};
`;

const BookCard = styled(motion.div)`
  background: linear-gradient(to right, #7c3aed, #3b82f6);
  color: white;
  border-radius: 1rem;
  padding: 1.5rem;
  box-shadow: 0 10px 15px rgba(0, 0, 0, 0.1);
`;

const SectionTitle = styled.h3`
  font-size: 1.25rem;
  font-weight: 700;
  color: #1f2937;
  margin-bottom: 1rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

// Component Logic
const BusinessPage = () => {
  const { id } = useParams();
  const business = businesses.find((b) => b.id === parseInt(id));

  const days = [
    "Søndag",
    "Mandag",
    "Tirsdag",
    "Onsdag",
    "Torsdag",
    "Fredag",
    "Lørdag",
  ];
  const currentDay = new Date().getDay();

  const handleContact = (type) => {
    let contactTypeNorwegian = type;
    if (type === "phone") contactTypeNorwegian = "telefon";
    if (type === "email") contactTypeNorwegian = "e-post";
    if (type === "website") contactTypeNorwegian = "nettside";
    if (type === "booking") contactTypeNorwegian = "booking";

    toast({
      title: "Kontaktinformasjon",
      description: `🚧 Direkte ${contactTypeNorwegian}-integrasjon er ikke implementert ennå—men ikke bekymre deg! Du kan be om det i din neste melding! 🚀`,
    });
  };

  if (!business) {
    return (
      <PageWrapper>
        <div
          style={{
            minHeight: "100vh",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div style={{ textAlign: "center" }}>
            <h1
              style={{
                fontSize: "1.5rem",
                fontWeight: "bold",
                color: "#111827",
                marginBottom: "1rem",
              }}
            >
              Bedrift ikke funnet
            </h1>
            <Link to="/">
              <Button>Tilbake til forsiden</Button>
            </Link>
          </div>
        </div>
      </PageWrapper>
    );
  }

  return (
    <PageWrapper>
      <StickyHeader>
        <div style={{ maxWidth: "1280px", margin: "0 auto", padding: "1rem" }}>
          <Link to="/">
            <Button variant="ghost">
              <ArrowLeft size={16} />
              <span style={{ marginLeft: "0.5rem" }}>
                Tilbake til bedrifter
              </span>
            </Button>
          </Link>
        </div>
      </StickyHeader>

      <Section>
        <div
          style={{
            maxWidth: "1280px",
            margin: "0 auto",
            display: "grid",
            gap: "3rem",
            gridTemplateColumns: "1fr 1fr",
          }}
        >
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
          >
            <CategoryTag>{business.category}</CategoryTag>
            <BusinessTitle>{business.name}</BusinessTitle>

            <div
              style={{
                display: "flex",
                alignItems: "center",
                marginBottom: "1.5rem",
              }}
            >
              {[...Array(5)].map((_, i) => (
                <Star
                  key={i}
                  size={20}
                  className={
                    i < business.rating
                      ? "text-yellow-400 fill-current"
                      : "text-gray-300"
                  }
                />
              ))}
              <span style={{ marginLeft: "0.5rem", fontWeight: "600" }}>
                {business.rating}
              </span>
              <span style={{ marginLeft: "0.5rem", color: "#6b7280" }}>
                ({business.reviews} anmeldelser)
              </span>
            </div>

            <p
              style={{
                color: "#4b5563",
                fontSize: "1.125rem",
                lineHeight: "1.75rem",
                marginBottom: "2rem",
              }}
            >
              {business.description}
            </p>

            <div style={{ display: "flex", flexWrap: "wrap", gap: "1rem" }}>
              <Button
                onClick={() => handleContact("phone")}
                style={{
                  background: "linear-gradient(to right, #22c55e, #16a34a)",
                  color: "white",
                  borderRadius: "9999px",
                  padding: "0.75rem 1.5rem",
                }}
              >
                <Phone size={16} />
                <span style={{ marginLeft: "0.5rem" }}>Ring nå</span>
              </Button>

              <Button
                variant="outline"
                onClick={() => handleContact("email")}
                style={{
                  borderRadius: "9999px",
                  borderColor: "#d8b4fe",
                  color: "#7c3aed",
                }}
              >
                <Mail size={16} />
                <span style={{ marginLeft: "0.5rem" }}>Send e-post</span>
              </Button>

              {business.website && (
                <Button
                  variant="outline"
                  onClick={() => handleContact("website")}
                  style={{
                    borderRadius: "9999px",
                    borderColor: "#93c5fd",
                    color: "#2563eb",
                  }}
                >
                  <Globe size={16} />
                  <span style={{ marginLeft: "0.5rem" }}>Besøk nettside</span>
                </Button>
              )}
            </div>
          </motion.div>

          <motion.div
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <div
              style={{
                borderRadius: "1.5rem",
                overflow: "hidden",
                boxShadow: "0 10px 25px rgba(0,0,0,0.1)",
              }}
            >
              <img
                src="https://images.unsplash.com/photo-1697256200022-f61abccad430"
                alt={`${business.name} hovedbilde`}
                style={{ width: "100%", height: "24rem", objectFit: "cover" }}
              />
            </div>
          </motion.div>
        </div>
      </Section>

      <Section>
        <div
          style={{
            maxWidth: "1280px",
            margin: "0 auto",
            display: "grid",
            gridTemplateColumns: "2fr 1fr",
            gap: "3rem",
          }}
        >
          {/* Add gallery, services, specialties */}
          {/* ... */}

          <div className="space-y-8">
            <InfoCard
              as={motion.div}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
            >
              <SectionTitle>
                <Clock /> Åpningstider
              </SectionTitle>
              {days.map((day, index) => (
                <DayRow key={day} highlight={index === currentDay}>
                  <DayText highlight={index === currentDay}>{day}</DayText>
                  <HourText highlight={index === currentDay}>
                    {business.openingHours[index] || "Stengt"}
                  </HourText>
                </DayRow>
              ))}
            </InfoCard>

            <BookCard
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.5 }}
            >
              <SectionTitle>
                <Calendar /> Bestill time
              </SectionTitle>
              <p style={{ marginBottom: "1rem", opacity: 0.9 }}>
                Klar for et besøk? Bestill time eller konsultasjon i dag!
              </p>
              <Button
                onClick={() => handleContact("booking")}
                style={{
                  width: "100%",
                  background: "white",
                  color: "#6b21a8",
                  borderRadius: "9999px",
                  padding: "0.75rem 1rem",
                }}
              >
                Bestill nå
              </Button>
            </BookCard>
          </div>
        </div>
      </Section>
    </PageWrapper>
  );
};

export default BusinessPage;
