import React, { useState } from "react";
import styled from "styled-components";
import { motion } from "framer-motion";
import { attractions } from "../data/attractions";

const Section = styled.section`
  padding: 4rem 1rem;
  background: #f8fafc;
`;

const Wrapper = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const Heading = styled.h2`
  font-size: 2rem;
  font-weight: bold;
  text-align: center;
  margin-bottom: 1rem;
`;

const Subheading = styled.p`
  font-size: 1.125rem;
  text-align: center;
  color: #4b5563;
  margin-bottom: 3rem;
`;

const Filters = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-bottom: 2rem;
  flex-wrap: wrap;
`;

const FilterButton = styled.button`
  padding: 0.5rem 1rem;
  border-radius: 9999px;
  border: none;
  background: ${({ active }) => (active ? "#2563eb" : "#e5e7eb")};
  color: ${({ active }) => (active ? "#fff" : "#1f2937")};
  cursor: pointer;
  font-size: 0.875rem;
  transition: background 0.3s ease;
`;

const Grid = styled.div`
  display: grid;
  gap: 2rem;
  grid-template-columns: 1fr;

  @media (min-width: 768px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (min-width: 1024px) {
    grid-template-columns: repeat(3, 1fr);
  }
`;

const Card = styled(motion.div)`
  background: white;
  border-radius: 1rem;
  overflow: hidden;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.05);
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 12px 30px rgba(0, 0, 0, 0.08);
  }
`;

const Image = styled.img`
  width: 100%;
  height: 230px;
  object-fit: cover;
`;

const CardContent = styled.div`
  padding: 1rem;
`;

const Category = styled.span`
  display: inline-block;
  font-size: 0.75rem;
  color: #7c3aed;
  background: #ede9fe;
  padding: 0.25rem 0.5rem;
  border-radius: 9999px;
  margin-bottom: 0.5rem;
`;

const Title = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const Description = styled.p`
  font-size: 0.875rem;
  color: #6b7280;
`;

const AttractionsPage = () => {
  const [selectedCategory, setSelectedCategory] = useState("Alle");

  const categories = ["Alle", "Historisk", "Festival", "Natur", "Museum"];

  const filteredAttractions =
    selectedCategory === "Alle"
      ? attractions
      : attractions.filter((item) => item.category === selectedCategory);

  return (
    <Section>
      <Wrapper>
        <Heading>Attraksjoner i Notodden</Heading>
        <Subheading>
          Oppdag historie, festivaler, natur og museer i Telemark og omegn.
        </Subheading>
        <Filters>
          {categories.map((cat) => (
            <FilterButton
              key={cat}
              active={selectedCategory === cat}
              onClick={() => setSelectedCategory(cat)}
            >
              {cat}
            </FilterButton>
          ))}
        </Filters>
        <Grid>
          {filteredAttractions.map((item, i) => (
            <Card
              key={item.id}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: i * 0.1 }}
            >
              <Image src={item.imageUrl} alt={item.title} />
              <CardContent>
                <Category>{item.category}</Category>
                <Title>{item.title}</Title>
                <Description>{item.description}</Description>
              </CardContent>
            </Card>
          ))}
        </Grid>
      </Wrapper>
    </Section>
  );
};

export default AttractionsPage;
