import React from "react";
import { useParams, Link } from "react-router-dom";
import { motion } from "framer-motion";
import {
  ArrowLeft,
  MapPin,
  Phone,
  Mail,
  Globe,
  Clock,
  Star,
  Calendar,
  CheckCircle,
} from "lucide-react";
import { businesses } from "../data/businesses";
import { toast } from "../components/ui/use-toast";
import styled from "styled-components";

// === Styled Components ===
const PageWrapper = styled.div`
  min-height: 100vh;
  background: #f8fafc;
`;

const Section = styled.section`
  padding: 2rem 1rem;
  max-width: 1280px;
  margin: auto;
  /* background-color: #fbfbfd; */
`;

const Hero = styled.div`
  display: flex;
  flex-direction: row;
  gap: 2rem;
  flex-wrap: wrap;
  align-items: start;
`;

const Left = styled.div`
  flex: 1;
`;

const Right = styled.div`
  flex: 1;
  min-width: 300px;

  img {
    height: 24rem;
    object-fit: cover;
    object-position: 50% 15%;
  }
`;

const Title = styled.h1`
  font-size: 2rem;
  font-weight: bold;
  color: #1f2937;
`;

const Rating = styled.div`
  display: flex;
  align-items: center;
  gap: 0.25rem;
  margin: 0.5rem 0 1.5rem 0;
`;

const Description = styled.p`
  color: #4b5563;
  margin-bottom: 1.5rem;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 0.5rem;
  flex-wrap: wrap;
`;

const ActionButton = styled.button`
  padding: 0.5rem 1rem;
  border-radius: 0.5rem;
  border: none;
  font-size: 0.875rem;
  cursor: pointer;
  transition: background 0.2s;
  background: ${(props) => props.bg || "#e0e7ff"};
  color: ${(props) => props.color || "#1f2937"};

  &:hover {
    opacity: 0.9;
  }
`;

const GalleryGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
  margin-top: 1rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const InfoCard = styled.div`
  background: white;
  padding: 1.5rem;
  border-radius: 1rem;
  box-shadow: 0 0 10px rgb(0 0 0 / 17%);
  margin-bottom: 1rem;

  p:nth-of-type(1) svg {
    color: #9333ea !important;
  }
`;

const DayRow = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0.5rem 0.75rem;
  background: ${(props) => (props.highlight ? "#f3e8ff" : "transparent")};
  border-radius: 0.5rem;
`;

const FlexGrid = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 2rem;
  margin-top: 2rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const BookingCTA = styled.div`
  background: #7c3aed;
  color: white;
  padding: 1.5rem;
  border-radius: 1rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 2rem;
  flex-wrap: wrap;
`;

const ContactCard = styled(InfoCard)`
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
  background: #f8fafc;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.05);
`;

const ContactRow = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 0.75rem;
`;

const IconWrapper = styled.div`
  margin-top: 0.15rem;
`;

const ContactText = styled.div`
  display: flex;
  flex-direction: column;

  strong {
    font-weight: 600;
    color: #111827;
    font-size: 0.95rem;
  }

  span {
    font-size: 0.95rem;
    color: #4b5563;
  }
`;

// === Main Component ===
const BusinessPage = () => {
  const { id } = useParams();
  const business = businesses.find((b) => b.id === parseInt(id));

  const days = [
    "Søndag",
    "Mandag",
    "Tirsdag",
    "Onsdag",
    "Torsdag",
    "Fredag",
    "Lørdag",
  ];
  const currentDay = new Date().getDay();

  const handleContact = (type) => {
    const messages = {
      phone: "telefon",
      email: "e-post",
      website: "nettside",
      booking: "booking",
    };
    toast({
      title: "Kontaktinformasjon",
      description: `🚧 Direkte ${messages[type]}-integrasjon er ikke implementert ennå.`,
    });
  };

  if (!business) {
    return (
      <PageWrapper>
        <Section>
          <h1>Bedrift ikke funnet</h1>
          <Link to="/">Tilbake til forsiden</Link>
        </Section>
      </PageWrapper>
    );
  }

  return (
    <PageWrapper>
      <Section>
        <Link to="/">
          <ActionButton>
            <ArrowLeft size={16} /> Tilbake
          </ActionButton>
        </Link>

        <Hero>
          <Left>
            <span
              style={{
                background: "#ede9fe",
                color: "#6b21a8",
                padding: "0.25rem 0.75rem",
                borderRadius: "9999px",
                fontSize: "0.875rem",
              }}
            >
              {business.category}
            </span>
            <Title>{business.name}</Title>
            <Rating>
              {[...Array(5)].map((_, i) => (
                <Star
                  key={i}
                  fill={i < business.rating ? "yellow" : "gray"}
                  color={i < business.rating ? "#facc15" : "#d1d5db"}
                />
              ))}
              <span style={{ marginLeft: "0.5rem", fontSize: "0.875rem" }}>
                ({business.reviews || 89} anmeldelser)
              </span>
            </Rating>
            <Description>{business.description}</Description>
            <ButtonGroup>
              <ActionButton
                bg="#22c55e"
                color="#fff"
                onClick={() => handleContact("phone")}
              >
                📞 Ring nå
              </ActionButton>
              <ActionButton
                bg="#f3e8ff"
                color="#6b21a8"
                onClick={() => handleContact("email")}
              >
                📧 Send e-post
              </ActionButton>
              {business.website && (
                <ActionButton
                  bg="#eff6ff"
                  color="#2563eb"
                  onClick={() => handleContact("website")}
                >
                  🌐 Besøk nettside
                </ActionButton>
              )}
            </ButtonGroup>
          </Left>

          <Right>
            <img
              src={
                business.heroImage ||
                "https://images.unsplash.com/photo-1697256200022-f61abccad430"
              }
              alt="Business"
              style={{ width: "100%", borderRadius: "1rem" }}
            />
          </Right>
        </Hero>
      </Section>

      <Section>
        <FlexGrid>
          {/* Left: Gallery */}
          <div>
            <h2>Galleri</h2>
            <GalleryGrid>
              {business.gallery.map((desc, i) => (
                <motion.div
                  key={i}
                  initial={{ opacity: 0, scale: 0.95 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ delay: i * 0.1 }}
                >
                  <img
                    src="https://images.unsplash.com/photo-1595872018818-97555653a011"
                    alt={desc}
                    style={{
                      width: "100%",
                      borderRadius: "1rem",
                      objectFit: "cover",
                    }}
                  />
                </motion.div>
              ))}
            </GalleryGrid>
          </div>

          {/* Right: Info and Opening Hours */}
          <div>
            <ContactCard>
              <h4>Kontaktinformasjon</h4>

              <ContactRow>
                <IconWrapper>
                  <MapPin size={18} color="#9333ea" />
                </IconWrapper>
                <ContactText>
                  <strong>Adresse</strong>
                  <span>{business.address}</span>
                </ContactText>
              </ContactRow>

              <ContactRow>
                <IconWrapper>
                  <Phone size={18} color="#16a34a" />
                </IconWrapper>
                <ContactText>
                  <strong>Telefon</strong>
                  <span>{business.phone}</span>
                </ContactText>
              </ContactRow>

              <ContactRow>
                <IconWrapper>
                  <Mail size={18} color="#2563eb" />
                </IconWrapper>
                <ContactText>
                  <strong>E-post</strong>
                  <span>{business.email}</span>
                </ContactText>
              </ContactRow>

              <ContactRow>
                <IconWrapper>
                  <Globe size={18} color="#7c3aed" />
                </IconWrapper>
                <ContactText>
                  <strong>Nettside</strong>
                  <span>{business.website}</span>
                </ContactText>
              </ContactRow>
            </ContactCard>

            <InfoCard>
              <h4>Åpningstider</h4>
              {days.map((day, i) => (
                <DayRow key={i} highlight={i === currentDay}>
                  <span>{day}</span>
                  <span>{business.openingHours[i] || "Stengt"}</span>
                </DayRow>
              ))}
            </InfoCard>
          </div>
        </FlexGrid>
      </Section>

      <Section>
        <h3>Tjenester og Spesialiteter</h3>
        <div className="services__">
          <div className="left__side">
            <h4>Tjenester</h4>
            {business.services.map((s, i) => (
              <p key={i}>
                <CheckCircle size={16} color="green" /> {s}
              </p>
            ))}
          </div>
          <div className="right__side">
            <h4 style={{ marginTop: "1rem" }}>Spesialiteter</h4>
            {business.specialties.map((s, i) => (
              <p key={i}>
                <Star size={16} color="gold" /> {s}
              </p>
            ))}
          </div>
        </div>
      </Section>

      <Section>
        <BookingCTA>
          <div>
            <strong>Bestill time</strong>
            <p>Klart for et besøk? Bestill time eller konsultasjon i dag!</p>
          </div>
          <button
            style={{
              background: "white",
              color: "#7c3aed",
              padding: "0.5rem 1rem",
              borderRadius: "0.5rem",
              border: "none",
            }}
          >
            Bestill nå
          </button>
        </BookingCTA>
      </Section>
    </PageWrapper>
  );
};

export default BusinessPage;
