import React from "react";
import { useParams, Link } from "react-router-dom";
import { motion } from "framer-motion";
import styled from "styled-components";
import {
  ArrowLeft,
  MapPin,
  Phone,
  Mail,
  Globe,
  Clock,
  Star,
  Calendar,
  CheckCircle,
} from "lucide-react";
import { businesses } from "../data/businesses";
import { toast } from "../components/ui/use-toast";

const PageWrapper = styled.div`
  min-height: 100vh;
  background: linear-gradient(to bottom right, #f8fafc, #eff6ff, #f5f3ff);
`;

const StickyHeader = styled.div`
  position: sticky;
  top: 4rem;
  z-index: 40;
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
`;

const Container = styled.div`
  max-width: 1280px;
  margin: 0 auto;
  padding: 1rem;
`;

const Section = styled.section`
  padding: 4rem 1rem;
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: 3rem;

  @media (min-width: 1024px) {
    grid-template-columns: repeat(${(props) => props.columns || 2}, 1fr);
  }
`;

const Tag = styled.span`
  display: inline-block;
  background: #ede9fe;
  color: #6b21a8;
  padding: 0.25rem 0.75rem;
  border-radius: 9999px;
  font-size: 0.875rem;
  font-weight: 500;
`;

const Heading = styled.h1`
  font-size: 2.25rem;
  font-weight: 700;
  color: #111827;
  margin-bottom: 1rem;
`;

const SubHeading = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1f2937;
  margin-bottom: 1rem;
`;

const Rating = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 1.5rem;

  .stars {
    display: flex;
    gap: 0.25rem;
  }

  .score {
    font-weight: 600;
    color: #111827;
  }

  .reviews {
    color: #4b5563;
  }
`;

const Description = styled.p`
  font-size: 1.125rem;
  color: #4b5563;
  margin-bottom: 2rem;
  line-height: 1.75;
`;

const StyledButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  border-radius: 9999px;
  font-weight: 500;
  border: ${(props) =>
    props.variant === "outline" ? "1px solid #e9d5ff" : "none"};
  background: ${(props) =>
    props.variant === "outline"
      ? "white"
      : "linear-gradient(to right, #22c55e, #16a34a)"};
  color: ${(props) => (props.variant === "outline" ? "#9333ea" : "white")};
  cursor: pointer;

  &:hover {
    background: ${(props) =>
      props.variant === "outline"
        ? "#f3e8ff"
        : "linear-gradient(to right, #16a34a, #15803d)"};
  }
`;

const Card = styled.div`
  background: white;
  border-radius: 1rem;
  box-shadow: 0 10px 15px rgba(0, 0, 0, 0.05);
  padding: 1.5rem;
`;

const ContactRow = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 0.75rem;

  & + & {
    margin-top: 1rem;
  }

  svg {
    margin-top: 0.25rem;
  }
`;

const DayRow = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0.5rem 0.75rem;
  border-radius: 0.5rem;

  ${(props) =>
    props.isToday &&
    `
    background: #faf5ff;
    border: 1px solid #e9d5ff;
  `}

  span:first-child {
    font-weight: 500;
    color: ${(props) => (props.isToday ? "#6b21a8" : "#374151")};
  }

  span:last-child {
    color: ${(props) => (props.isToday ? "#7e22ce" : "#6b7280")};
    font-weight: ${(props) => (props.isToday ? "600" : "normal")};
  }
`;

const BookCard = styled(Card)`
  background: linear-gradient(to right, #7c3aed, #2563eb);
  color: white;
`;

const GalleryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 1rem;
`;

const GalleryImage = styled.img`
  width: 100%;
  height: 16rem;
  object-fit: cover;
  border-radius: 0.75rem;
`;

const ListItem = styled.li`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: #4b5563;
  margin-bottom: 0.5rem;
`;

const BusinessPage = () => {
  const { id } = useParams();
  const business = businesses.find((b) => b.id === parseInt(id));

  if (!business) {
    return (
      <PageWrapper>
        <Container
          style={{
            minHeight: "100vh",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <div style={{ textAlign: "center" }}>
            <h1
              style={{
                fontSize: "1.5rem",
                fontWeight: "bold",
                color: "#111827",
                marginBottom: "1rem",
              }}
            >
              Bedrift ikke funnet
            </h1>
            <Link to="/">
              <StyledButton variant="outline">
                Tilbake til forsiden
              </StyledButton>
            </Link>
          </div>
        </Container>
      </PageWrapper>
    );
  }

  const days = [
    "Søndag",
    "Mandag",
    "Tirsdag",
    "Onsdag",
    "Torsdag",
    "Fredag",
    "Lørdag",
  ];
  const currentDay = new Date().getDay();

  const handleContact = (type) => {
    let contactTypeNorwegian = type;
    if (type === "phone") contactTypeNorwegian = "telefon";
    if (type === "email") contactTypeNorwegian = "e-post";
    if (type === "website") contactTypeNorwegian = "nettside";
    if (type === "booking") contactTypeNorwegian = "booking";

    toast({
      title: "Kontaktinformasjon",
      description: `🚧 Direkte ${contactTypeNorwegian}-integrasjon er ikke implementert ennå—men ikke bekymre deg! Du kan be om det i din neste melding! 🚀`,
    });
  };

  return (
    <PageWrapper>
      <StickyHeader>
        <Container>
          <Link to="/">
            <StyledButton variant="outline">
              <ArrowLeft size={16} />
              <span>Tilbake til bedrifter</span>
            </StyledButton>
          </Link>
        </Container>
      </StickyHeader>

      <Section>
        <Container>
          <Grid>
            <motion.div
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
            >
              <Tag>{business.category}</Tag>
              <Heading>{business.name}</Heading>
              <Rating>
                <div className="stars">
                  {[...Array(5)].map((_, i) => (
                    <Star
                      key={i}
                      size={20}
                      color={
                        i < Math.floor(business.rating) ? "#facc15" : "#d1d5db"
                      }
                      fill={
                        i < Math.floor(business.rating) ? "#facc15" : "none"
                      }
                    />
                  ))}
                </div>
                <span className="score">{business.rating}</span>
                <span className="reviews">
                  ({business.reviews} anmeldelser)
                </span>
              </Rating>
              <Description>{business.description}</Description>
              <div style={{ display: "flex", flexWrap: "wrap", gap: "1rem" }}>
                <StyledButton onClick={() => handleContact("phone")}>
                  <Phone size={16} />
                  <span>Ring nå</span>
                </StyledButton>
                <StyledButton
                  variant="outline"
                  onClick={() => handleContact("email")}
                >
                  <Mail size={16} />
                  <span>Send e-post</span>
                </StyledButton>
                {business.website && (
                  <StyledButton
                    variant="outline"
                    onClick={() => handleContact("website")}
                  >
                    <Globe size={16} />
                    <span>Besøk nettside</span>
                  </StyledButton>
                )}
              </div>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              <img
                src="https://images.unsplash.com/photo-1697256200022-f61abccad430"
                alt={`${business.name} hovedbilde`}
                style={{
                  width: "100%",
                  height: "24rem",
                  objectFit: "cover",
                  borderRadius: "1.5rem",
                  boxShadow: "0 10px 15px rgba(0, 0, 0, 0.1)",
                }}
              />
            </motion.div>
          </Grid>
        </Container>
      </Section>

      <Section>
        <Container>
          <SubHeading>Bildegalleri</SubHeading>
          <GalleryGrid>
            {business.gallery.map((url, index) => (
              <GalleryImage
                key={index}
                src={url}
                alt={`Galleri ${index + 1}`}
              />
            ))}
          </GalleryGrid>
        </Container>
      </Section>

      <Section>
        <Container>
          <SubHeading>Tjenester</SubHeading>
          <ul>
            {business.services.map((service, index) => (
              <ListItem key={index}>
                <CheckCircle size={18} color="#10b981" />
                {service}
              </ListItem>
            ))}
          </ul>
        </Container>
      </Section>

      <Section>
        <Container>
          <SubHeading>Kontaktinfo</SubHeading>
          <Card>
            <ContactRow>
              <MapPin size={20} />
              <span>{business.address}</span>
            </ContactRow>
            <ContactRow>
              <Phone size={20} />
              <span>{business.phone}</span>
            </ContactRow>
            <ContactRow>
              <Mail size={20} />
              <span>{business.email}</span>
            </ContactRow>
            {business.website && (
              <ContactRow>
                <Globe size={20} />
                <span>{business.website}</span>
              </ContactRow>
            )}
          </Card>
        </Container>
      </Section>

      <Section>
        <Container>
          <SubHeading>Åpningstider</SubHeading>
          <Card>
            {business.hours ? (
              Object.entries(business.hours).map(([dayIndex, hours]) => (
                <DayRow
                  key={dayIndex}
                  isToday={parseInt(dayIndex) === currentDay}
                >
                  <span>{days[parseInt(dayIndex)]}</span>
                  <span>{hours}</span>
                </DayRow>
              ))
            ) : (
              <p>Ingen åpningstider registrert.</p>
            )}
          </Card>
        </Container>
      </Section>

      <Section>
        <Container>
          <SubHeading>Bestill time</SubHeading>
          <BookCard>
            <p style={{ fontSize: "1.125rem", marginBottom: "1rem" }}>
              Klar for å bestille en time hos {business.name}?
            </p>
            <StyledButton onClick={() => handleContact("booking")}>
              <Calendar size={16} />
              <span>Gå til booking</span>
            </StyledButton>
          </BookCard>
        </Container>
      </Section>
    </PageWrapper>
  );
};

export default BusinessPage;
