// ContactPage.jsx
import React, { useState } from "react";
import { motion } from "framer-motion";
import styled from "styled-components";
import { Mail, Phone, MapPin, Send, MessageCircle } from "lucide-react";
import { toast } from "../components/ui/use-toast";

// Styled Components
const PageWrapper = styled.div`
  min-height: 100vh;
  background: linear-gradient(to bottom right, #f8fafc, #ebf4ff, #f3e8ff);
`;

const HeroSection = styled.section`
  position: relative;
  padding: 5rem 1rem;
  overflow: hidden;
  background: linear-gradient(to bottom right, #7e22ce, #2563eb, #4338ca);
  color: white;
`;

const ContentSection = styled.section`
  position: relative;
  z-index: 10;
  margin-top: -2.5rem;
  padding: 4rem 1rem;
`;

const Container = styled.div`
  max-width: 1120px;
  margin: 0 auto;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  font-weight: bold;
  margin-bottom: 1.5rem;

  span {
    background: linear-gradient(to right, #fde68a, #fb923c);
    background-clip: text;
    -webkit-text-fill-color: transparent;
  }
`;

const ContactCard = styled(motion.div)`
  background: white;
  padding: 2rem;
  border-radius: 1rem;
  box-shadow: 0 10px 15px rgba(0, 0, 0, 0.1);
  text-align: center;
`;

const IconCircle = styled.div`
  width: 4rem;
  height: 4rem;
  border-radius: 9999px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 1rem;
  background-color: ${({ color }) => color || "rgba(0,0,0,0.05)"};
`;

const FormWrapper = styled(motion.div)`
  background: white;
  padding: 2rem;
  border-radius: 1rem;
  box-shadow: 0 10px 15px rgba(0, 0, 0, 0.1);
`;

const Input = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 0.5rem;
`;

const Textarea = styled.textarea`
  width: 100%;
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 0.5rem;
